# packagesimport csvimport randomimport copyimport matplotlib.pyplot as pltimport numpy as npimport sysimport win32com.clienta = 1.1532b = 2.0982outfile = 'riskDayLoadIn.csv'engine = win32com.client.Dispatch("OpenDSSEngine.DSS")engine.Start("0")results = []for mc in range(0,10):    for i in range(1,56):                with open('household-profiles/'+str(i)+'.csv','w') as csvfile:            writer = csv.writer(csvfile)            for j in range(0,1440):                writer.writerow([np.random.gamma(a,scale=1/b)])    engine.text.Command='clear'    circuit = engine.ActiveCircuit    engine.text.Command='compile master.dss'    engine.Text.Command='Export mon LINE1_PQ_vs_Time'        with open('LVTest_Mon_line1_pq_vs_time.csv','rU') as csvfile:        reader = csv.reader(csvfile)        next(reader)        for row in reader:            results.append(-float(row[2])-float(row[4])-float(row[6]))with open(outfile,'w') as csvfile:    writer = csv.writer(csvfile)    for row in results:        writer.writerow([row])