# packagesimport csvimport randomimport copyimport matplotlib.pyplot as pltimport numpy as npimport sysimport win32com.clienta = 1.1532b = 2.0982outfile = 'riskDayLoadIn.csv'loads = 'riskDaySpecifiedVsAppliedLoaad.csv'checkLoads = Trueengine = win32com.client.Dispatch("OpenDSSEngine.DSS")engine.Start("0")results = []if checkLoads == True:    appl = []    spec = []for mc in range(0,10):    for i in range(1,56):                with open('household-profiles/'+str(i)+'.csv','w') as csvfile:            writer = csv.writer(csvfile)            for j in range(0,1440):                ran = np.random.gamma(a,scale=1/b)                writer.writerow([ran])                if checkLoads == True:                    spec.append(ran)    engine.text.Command='clear'    circuit = engine.ActiveCircuit    engine.text.Command='compile master.dss'    engine.Text.Command='Export mon LINE1_PQ_vs_Time'        with open('LVTest_Mon_line1_pq_vs_time.csv','rU') as csvfile:        reader = csv.reader(csvfile)        next(reader)        for row in reader:            results.append(-float(row[2])-float(row[4])-float(row[6]))    if checkLoads == True:        for hh in range(1,56):            engine.Text.Command='Export mon hh'+str(hh)+'_pQ_vs_time'            with open('LVTest_Mon_hh'+str(hh)+'_pq_vs_time.csv','rU') as csvfile:                reader = csv.reader(csvfile)                next(reader)                for row in reader:                    appl.append(float(row[2]))with open(outfile,'w') as csvfile:    writer = csv.writer(csvfile)    for row in results:        writer.writerow([row])if checkLoads == True:    with open(loads,'w') as csvfile:        writer = csv.writer(csvfile)        writer.writerow(['Specified Load','Applied Load'])        for i in range(len(spec)):            writer.writerow([spec[i],appl[i]])